    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        Integer talla;
        String tipo;
        String nombre;
        String desc;
        Integer cant;
        Integer precio;

        talla = Integer.parseInt(txtTalla.getText());
        tipo = txtTalla.getText();
        nombre = txtTalla.getText();
        desc = txtTalla.getText();
        cant = Integer.parseInt(txtTalla.getText());
        precio = Integer.parseInt(txtTalla.getText());

        camisa = new Camisa(talla, tipo, nombre, desc, cant, precio);

    }//GEN-LAST:event_jButton1ActionPerformed

